// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { db, auth } from './firebaseConfig';
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import { createUserWithEmailAndPassword, signInWithEmailAndPassword, signOut, onAuthStateChanged } from "firebase/auth";
import {
  Container, TextField, Button, Typography, Card, CardContent, CardActions, Grid, AppBar, Toolbar, IconButton
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';

const App = () => {
  const [product, setProduct] = useState('');
  const [results, setResults] = useState([]);
  const [trackedProducts, setTrackedProducts] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState(null);

  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      if (currentUser) {
        fetchTrackedProducts(currentUser.uid);
      }
    });
  }, []);

  const searchProduct = async () => {
    const options = {
      method: 'GET',
      url: 'https://api.example.com/search', // Replace with your actual API URL
      params: { query: product },
      headers: {
        'X-RapidAPI-Key': 'YOUR_RAPIDAPI_KEY',
        'X-RapidAPI-Host': 'YOUR_RAPIDAPI_HOST'
      }
    };

    try {
      const response = await axios.request(options);
      setResults(response.data.products);
    } catch (error) {
      console.error(error);
    }
  };

  const trackProduct = async (product) => {
    if (!user) {
      alert('Please sign in to track products');
      return;
    }
    try {
      await addDoc(collection(db, "trackedProducts"), {
        uid: user.uid,
        name: product.name,
        price: product.price,
        url: product.url
      });
      fetchTrackedProducts(user.uid);
    } catch (e) {
      console.error("Error adding document: ", e);
    }
  };

  const fetchTrackedProducts = async (uid) => {
    const q = query(collection(db, "trackedProducts"), where("uid", "==", uid));
    const querySnapshot = await getDocs(q);
    const products = [];
    querySnapshot.forEach((doc) => {
      products.push(doc.data());
    });
    setTrackedProducts(products);
  };

  const register = async () => {
    try {
      await createUserWithEmailAndPassword(auth, email, password);
    } catch (error) {
      console.error(error.message);
    }
  };

  const login = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (error) {
      console.error(error.message);
    }
  };

  const logout = async () => {
    await signOut(auth);
  };

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" style={{ flexGrow: 1 }}>
            Smart Shopping Assistant
          </Typography>
          {user ? (
            <>
              <Typography variant="h6">{user.email}</Typography>
              <Button color="inherit" onClick={logout}>Logout</Button>
            </>
          ) : (
            <>
              <TextField
                label="Email"
                type="email"
                variant="outlined"
                size="small"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <TextField
                label="Password"
                type="password"
                variant="outlined"
                size="small"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <Button color="inherit" onClick={login}>Login</Button>
              <Button color="inherit" onClick={register}>Register</Button>
            </>
          )}
        </Toolbar>
      </AppBar>

      <div style={{ marginTop: 20 }}>
        <TextField
          fullWidth
          label="Search for a product"
          variant="outlined"
          value={product}
          onChange={(e) => setProduct(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          startIcon={<SearchIcon />}
          onClick={searchProduct}
          style={{ marginTop: 10 }}
        >
          Search
        </Button>
      </div>

      <Grid container spacing={2} style={{ marginTop: 20 }}>
        <Grid item xs={12}>
          <Typography variant="h5">Search Results</Typography>
          {results.map((result) => (
            <Card key={result.id} style={{ marginTop: 10 }}>
              <CardContent>
                <Typography variant="h6">{result.name}</Typography>
                <Typography color="textSecondary">${result.price}</Typography>
              </CardContent>
              <CardActions>
                <Button size="small" color="primary" onClick={() => trackProduct(result)}>Track Price</Button>
              </CardActions>
            </Card>
          ))}
        </Grid>
      </Grid>

      <Grid container spacing={2} style={{ marginTop: 20 }}>
        <Grid item xs={12}>
          <Typography variant="h5">Tracked Products</Typography>
          {trackedProducts.map((product, index) => (
            <Card key={index} style={{ marginTop: 10 }}>
              <CardContent>
                <Typography variant="h6">{product.name}</Typography>
                <Typography color="textSecondary">${product.price}</Typography>
                <a href={product.url} target="_blank" rel="noopener noreferrer">Buy Now</a>
              </CardContent>
            </Card>
          ))}
        </Grid>
      </Grid>
    </Container>
  );
};

export default App;
