import React from 'react';
import { useHistory } from 'react-router-dom';
import { Button, Typography, Container } from '@mui/material';
import './WelcomeBanner.css';

const WelcomeBanner = () => {
  const history = useHistory();

  const handleGetStarted = () => {
    history.push('/next-page'); // Replace with your actual target route
  };

  return (
    <div className="welcome-banner">
      <Container>
        <Typography variant="h2" className="welcome-message">
          Welcome to the Smart Shopping Assistant
        </Typography>
        <Button variant="contained" color="primary" onClick={handleGetStarted} className="get-started-button">
          Get Started
        </Button>
      </Container>
    </div>
  );
};

export default WelcomeBanner;
